#include <stdio.h>
#include <stdlib.h>


int main() {
	const char Text[] = "";
	const char Pattern[] = "GAGCAAAGA";
	int TextSize = sizeof(Text);
	int PatternSize = sizeof(Pattern);
	printf("TextSize is %d \n PatternSize is %d \n", TextSize, PatternSize);
	int i;
	static int count = 0;
	for(i = 0; i < (TextSize - PatternSize); i++) {

		int isEqual = 1;
		int j = 0;
		for(j = 0; j < PatternSize-1; j++) {
			printf("%c   %c \n", Text[i+j], Pattern[j]);
			if(Text[i+j] != Pattern[j]) {
				isEqual = 0;
				printf("here \n");
				break;
			}
		}
		if(isEqual == 1) {
			printf("in here \n");
			count = count + 1;
		}
	}
	printf("%d \n", count);

	return 0;
}